import jsonimport matplotlib.pyplot as pltfrom funcs import string_to_date_arr, convert_values_to_diffwith open('stocks.json', 'r') as file:    my_stocks = json.load(file)    sp500 = my_stocks["^GSPC"]ticker = my_stocks["BA"]x_values = string_to_date_arr(sp500.keys())y1_values = convert_values_to_diff(sp500.values())y2_values = convert_values_to_diff(ticker.values())#Plot vertical linevx = [x_values[-1], x_values[-1]]vy = [y1_values[-1], y2_values[-1]]plt.plot(x_values, y1_values, 'r', label="SP500")plt.plot(x_values, y2_values, 'b', label="BA")plt.legend()plt.xlabel('Date')plt.ylabel('Closing Price')plt.title("SP500" + ' vs. ' + "AAPL")plt.show()