import unittestimport requestsimport jsonimport sysserver = sys.argv[1]class TestAPI(unittest.TestCase):        def test_flask(self):        response = requests.get('http://' + server + ':5000/')        self.assertEqual(response.status_code, 200)        #The following four tests tests the get point route    def test_stock_point(self):        response = requests.get('http://' + server + ':5000/stock/AAPL')        self.assertEqual(response.status_code, 200)        data = response.json()                self.assertEqual(len(data) > 100, True)        self.assertEqual(type(data), dict)        self.assertEqual(data["2023-09-22"], 174.79)            def test_stock_point_query(self):        response = requests.get('http://' + server + ':5000/stock/AAPL?date=2023-09-22')        self.assertEqual(response.status_code, 200)        data = response.json()        self.assertEqual(data, "2023-09-22: 174.79")            def test_invalid_stock_point(self):        response = requests.get('http://' + server + ':5000/stock/AA')        self.assertEqual(response.status_code, 404)            def test_invalid_stock_query(self):        response = requests.get('http://' + server + ':5000/stock/AAPL?date=2022-09-2')        self.assertEqual(response.status_code, 404)            #The following tests test the add_point route    def test_add_point(self):        url = 'http://' + server + ':5000/stock/add'        job = {"ticker": "AAPL", 'date': '2023-11-10', "value": 186.4}                response = requests.post(url, json=job)        self.assertEqual(response.status_code,200)        #self.assertEqual(response.json(), 'Data point created for AAPL: 2024-11-05:200')            #The following tests test the diff route    def test_stock_diff(self):        response = requests.get('http://' + server + ':5000/stock/diff/AAPL/1y')        self.assertEqual(response.status_code, 200)        def test_stock_diff_invalid_period(self):        response = requests.get('http://' + server + ':5000/stock/diff/AAPL/6y')        self.assertEqual(response.status_code, 404)            #The following tests will test the graph post route    def test_stock_graph(self):        url = 'http://' + server + ':5000/stock/graph'        job = {"ticker": "AAPL", "period": "1y"}                response = requests.post(url, json=job)        self.assertEqual(response.status_code, 200)                data = response.json()        self.assertEqual(data['job_type'], 'graph' )        self.assertEqual(json.loads(data['input_values'])['ticker'], 'AAPL')        self.assertEqual(json.loads(data['input_values'])['period'], "1y" )                response2 = requests.get('http://' + server + ':5000/jobs/' + data['id'])        self.assertEqual(response2.status_code, 200)        data2 = response2.json()                response3 = requests.get('http://' + server + ':5000/download/' + data['id'])            def test_stock_compare(self):        url = 'http://' + server + ':5000/stock/compare'        job = {"ticker1": "AAPL", "ticker2": "ADBE", "period":"1y"}                response = requests.post(url, json=job)        data = response.json()        self.assertEqual(response.status_code, 200)        self.assertEqual(data['job_type'], 'compare')        self.assertEqual(json.loads(data['input_values'])['ticker1'], 'AAPL')        self.assertEqual(json.loads(data['input_values'])['ticker2'], 'ADBE')                response2 = requests.get('http://' + server + ':5000/jobs/' + data['id'])        self.assertEqual(response2.status_code, 200)                response3 = requests.get('http://' + server + ':5000/download/' + data['id'])    def test_stock_sp500_compare(self):        url = 'http://' + server + ':5000/stock/compare_sp500'        job = {"ticker": "BA", "period": "1y"}                response = requests.post(url, json=job)        self.assertEqual(response.status_code, 200)                data = response.json()        s